<?include("iconnect.php")?><? $pname = $_REQUEST['pname'];$page = $_REQUEST['page'];$brandsno = $_REQUEST["brandsno"];$brandsno=str_replace("'","&#39;",$brandsno);$where="brandsno =$brandsno";$result_brands = $obj->select_all_values('brands', $where,'');foreach($result_brands as $brands_row){	$brandslogo = $brands_row['brandslogo'];	$brandsbanner = $brands_row['brandsbanner'];	if ($brandslogo<>"") 	{ 	unlink("../brandslogo/".$brandslogo);	}	if ($brandsbanner<>"") 	{ 	unlink("../brandsbanner/".$brandsbanner);	}	}$brandswherecondition="brandsno=$brandsno ";$brandsresult=$obj->delete_condition("brands", $brandswherecondition);if($brandsresult>0 ){	header("Location:brands.php?action=deleted&brandsno=$brandsno&pname=$pname&page=$page");	ob_flush(); }?>