<?include("iconnect.php")?><? $pname = $_REQUEST['pname'];$page = $_REQUEST['page'];$clientsno = $_REQUEST["clientsno"];$clientsno=str_replace("'","&#39;",$clientsno);$where="clientsno =$clientsno";$result_clients = $obj->select_all_values('clients', $where,'');foreach($result_clients as $clients_row){	$clientslogo = $clients_row['clientslogo'];	$clientsbanner = $clients_row['clientsbanner'];	if ($clientslogo<>"") 	{ 	unlink("../clientslogo/".$clientslogo);	}	if ($clientsbanner<>"") 	{ 	unlink("../clientsbanner/".$clientsbanner);	}	}$clientswherecondition="clientsno=$clientsno ";$clientsresult=$obj->delete_condition("clients", $clientswherecondition);if($clientsresult>0 ){	header("Location:clients.php?action=deleted&clientsno=$clientsno&pname=$pname&page=$page");	ob_flush(); }?>